import { useCallback, useRef } from 'react';

interface UseRefCallbackParams {
 ref?: React.MutableRefObject;
 onMount?: (node: T) => void;
 onUnmount?: (node: T) => void;
}

// Generic syntax inspired by: https://stackoverflow.com/questions/32308370/what-is-the-syntax-for-typescript-arrow-functions-with-generics
export const useRefCallback = ({
 ref,
 onMount,
 onUnmount,
} : UseRefCallbackParams ) => {
 // https://medium.com/welldone-software/usecallback-might-be-what-you-meant-by-useref-useeffect-773bc0278ae
 const nodeRef = ref || useRef();

 const setRef = useCallback(
 (node: T) => {
 if (nodeRef.current) {
 // Make sure to cleanup any events/references added to the last instance
 onUnmount && onUnmount(nodeRef.current);
 }

 nodeRef.current = node;

 if (nodeRef.current) {
 // Check if a node is actually passed. Otherwise node would be null.
 // You can now do what you need to, addEventListeners, measure, etc.
 onMount && onMount(nodeRef.current);
 }
 },
 [onUnmount, onMount]
 );

 return setRef;
};

